return: db 10
LEN: EQU ($ - surprise)
ROWS: EQU 24
COLS: EQU 80

SECTION .text
global _main
_main:

; Set up for row major traversal of surprise
mov ecx, ROWS
mov ebx, surprise

outer:
	push ecx
	mov ecx, COLS

	inner:
		call decode	;;; Decode current character
		call print	;;; Print current character
		inc ebx
		break0:
		loop inner

	push ebx
	mov ebx, return
	call print	;;; Prints new line
	pop ebx

	pop ecx
	loop outer

lastBreak:
	
mov eax, 1
mov ebx, 0
int 80h

; Decode function: Takes ecx and ebx as parameters
; ecx has reverse column number
; ebx has address of current character to decode
decode:
	pusha
		
	mov eax, COLS
	sub eax, ecx		;;; Reverses column number to proper position
	
	sub [ebx], eax	;;; Decode character

	popa
	
	ret

; Print function: Takes ebx as parameter
; ebx has address of current character
print:
	pusha

	xchg ecx, ebx	;; ecx has column number, whereas ebx has character address

	mov eax, 4	;; syswrite
	mov ebx, 1	;; stdout
			;; ecx has character address
	mov edx, 1	;; Character of length 1
	
	int 80h

	popa

	ret
